import React, { useState } from "react";
import "./style.css";

function App() {
  const [input, setInput] = useState("");
  const [messages, setMessages] = useState([]);

  const sendMessage = async () => {
    if (!input.trim()) return;
    const userMsg = { sender: "user", text: input };
    setMessages(m => [...m, userMsg]);

    const res = await fetch("http://127.0.0.1:5000/api/correct", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ text: input })
    });

    const data = await res.json();
    const botMsg = { sender: "bot", text: data.corrected };
    setMessages(m => [...m, botMsg]);
    setInput("");
  };

  return (
    <div className="chat-container">
      <div className="messages">
        {messages.map((m, i) => (
          <div key={i} className={m.sender === "user" ? "user-msg" : "bot-msg"}>
            {m.text}
          </div>
        ))}
      </div>

      <div className="input-box">
        <input value={input} onChange={e => setInput(e.target.value)} placeholder="Type text to autocorrect..." />
        <button onClick={sendMessage}>Send</button>
      </div>
    </div>
  );
}

export default App;
